    static long getFloorPart1(String input) {
        long upCount = input.chars().filter(instruction -> instruction == '(').count();
        long downCount = input.chars().filter(instruction -> instruction == ')').count();
        return upCount - downCount;
    }

    static long getPositionOfCharFirstGoingToMinus1Part2(String input) {
        long upCount = 0;
        long downCount = 0;
        int positionOfCharFirstGoingToMinus1 = 0;

        for (int instruction : input.chars().toArray()) {
            positionOfCharFirstGoingToMinus1++;
            if (instruction == ')') {
                downCount++;
            } else if (instruction == '('){
                upCount++;
            }
            if (upCount - downCount == -1) {
                break;
            }
        }

        return positionOfCharFirstGoingToMinus1;
    }
